using System;
using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.Extensions.Caching.Memory;
using Microsoft.Net.Http.Headers;
using static CitizenFX.Core.Native.API;

namespace FxWebAdmin
{
    public class IconController : Controller
    {
        private IMemoryCache cache;

        public IconController(IMemoryCache cache)
        {
            this.cache = cache;
        }

        [AllowAnonymous]
        [HttpGet("~/icon.png")]
        public IActionResult GetIcon()
        {
            if (!cache.TryGetValue("icon", out string iconString))
            {
                iconString = GetConvar("sv_icon", "");

                if (string.IsNullOrWhiteSpace(iconString))
                {
                    iconString = "";
                }

                cache.Set("icon", iconString, TimeSpan.FromMinutes(15));
            }

            return File(
                Convert.FromBase64String(iconString),
                "image/png",
                DateTimeOffset.UtcNow - TimeSpan.FromHours(2),
                new EntityTagHeaderValue($"\"{GetHashKey(iconString):X}\""),
                true);
        }
    }
}